package action;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.ResponseBody;import java.util.Map;public class TodoController {@RequestMapping(value="/createtask/{type}/", method = RequestMethod.GET)    public @ResponseBody Map<String,Object> createTask (@PathVariable String type){    return new TodoHelper().createTask(type); }}